mapper.select:
SELECT * FROM deploy
WHERE 1 = 1

        AND tenant_code = #{tenantCode}


        AND deploy_code = #{deployCode}


        AND machine_code = #{machineCode}

SELECT *
FROM deploy
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND deploy_code = #{deployCode}


        AND machine_code = #{machineCode}


        AND shop_code = #{shopCode}


        AND state = #{state}

mapper.insert:
INSERT INTO deploy (
    gmt_created
    , gmt_modified
    , tenant_code
    , extra_info
    , deploy_code
    , machine_code
    , model_code
    , shop_code
    , state
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
    , #{deployCode}
    , #{machineCode}
    , #{modelCode}
    , #{shopCode}
    , #{state}
)

mapper.update:
UPDATE deploy
SET
    gmt_modified = now()

        , model_code = #{modelCode}


        , machine_code = #{machineCode}


        , shop_code = #{shopCode}


        , state = #{state}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1

        AND tenant_code = #{tenantCode}

    AND deploy_code = #{deployCode}

mapper.delete:
DELETE FROM deploy
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND deploy_code = #{deployCode}
