mapper.insert:
INSERT INTO model (
    gmt_created
    , gmt_modified
    , model_code
    , enable_flow_all
    , pipe_clean_simult_max_cnt
    , extra_info
) VALUES (
    now()
    , now()
    , #{modelCode}
    , #{enableFlowAll}
    , #{pipeCleanSimultMaxCnt}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE model
SET
    gmt_modified = now()

        , enable_flow_all = #{enableFlowAll}


        , pipe_clean_simult_max_cnt = #{pipeCleanSimultMaxCnt}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    AND model_code = #{modelCode};

mapper.delete:
DELETE FROM model
WHERE 1 = 1
    AND model_code = #{model_code}
