mapper.select:
SELECT * FROM model_pipeline
WHERE 1 = 1
    AND model_code = #{modelCode}
    AND pipeline_num = #{pipelineNum}

mapper.insert:
INSERT INTO model_pipeline (
    gmt_created
    , gmt_modified
    , model_code
    , pipeline_num
    , enable_warm
    , enable_freeze
    , capacity
    , syrup_pipeline
    , sensor_threshold
) VALUES (
    now()
    , now()
    , #{modelCode}
    , #{pipelineNum}
    , #{enableWarm}
    , #{enableFreeze}
    , #{capacity}
    , #{syrupPipeline}
    , #{sensorThreshold}
)

mapper.update:
UPDATE model_pipeline
SET
    gmt_modified = now()

        , enable_warm = #{enableWarm}


        , enable_freeze = #{enableFreeze}


        , capacity = #{capacity}


        , syrup_pipeline = #{syrupPipeline}


        , sensor_threshold = #{sensorThreshold}

WHERE model_code = #{modelCode}
    AND pipeline_num = #{pipelineNum}

mapper.delete:
DELETE FROM model_pipeline
WHERE 1 = 1
    AND model_code = #{modelCode}
