mapper.select:
SELECT * FROM machine
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND machine_code = #{machineCode}


        AND machine_name = #{machineName}

SELECT * FROM machine
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

SELECT * FROM machine
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND screen_code = #{screenCode}


        AND elec_board_code = #{elecBoardCode}


        AND model_code = #{modelCode}


        AND shop_code = #{shopCode}

mapper.insert:
INSERT INTO machine (
    gmt_created
    , gmt_modified
    , machine_code
    , screen_code
    , elec_board_code
    , model_code
    , machine_name
    , state
    , valid_until
    , maintain_until
    , shop_code
    , tenant_code
    , extra_info
) VALUES (
    now()
    , now()
    , #{machineCode}
    , #{screenCode}
    , #{elecBoardCode}
    , #{modelCode}
    , #{machineName}
    , #{state}
    , #{validUntil}
    , #{maintainUntil}
    , #{shopCode}
    , #{tenantCode}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE machine
SET
    gmt_modified = now()

        ,screen_code = #{screenCode}


        , elec_board_code = #{elecBoardCode}


        , model_code = #{modelCode}


        , machine_name = #{machineName}


        , state = #{state}


        , valid_until = #{validUntil}


        , maintain_until = #{maintainUntil}


        , shop_code = #{shopCode}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND machine_code = #{machineCode}

mapper.delete:
DELETE FROM machine
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND machine_code = #{machineCode}
