mapper.select:
SELECT * FROM machine_model_pipeline
WHERE 1 = 1
    AND model_code = #{modelCode}
    AND pipeline_num = #{pipelineNum}

mapper.insert:
INSERT INTO `machine_model_pipeline` (
    `gmt_created`
    , `gmt_modified`
    , `model_code`
    , `pipeline_num`
    , `enable_warm`
    , `enable_freeze`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{modelCode}
    , #{pipelineNum}
    , #{enableWarm}
    , #{enableFreeze}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE machine_model_pipeline
SET
    gmt_modified = now()

        , enable_warm = #{enableWarm}


        , enable_freeze = #{enableFreeze}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE model_code = #{modelCode}
    AND pipeline_num = #{pipelineNum}

mapper.delete:
DELETE FROM machine_model_pipeline
WHERE 1 = 1
    AND model_code = #{modelCode}
