mapper.select:
SELECT * FROM accuracy_template_topping
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND template_code = #{templateCode}

mapper.insert:
INSERT INTO accuracy_template_topping (
    gmt_created
    , gmt_modified
    , tenant_code
    , template_code
    , topping_code
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{templateCode}
    , #{toppingCode}
)

mapper.delete:
DELETE FROM accuracy_template_topping
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND template_code = #{templateCode}
