mapper.select:
SELECT * FROM tea
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND tea_code = #{teaCode}

SELECT * FROM tea
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

SELECT COUNT(1) FROM tea
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND tea_type_code = #{teaTypeCode}

SELECT * FROM tea
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND tea_code = #{teaCode}


        AND tea_name = #{teaName}

mapper.insert:
INSERT INTO tea (
    gmt_created
    , gmt_modified
    , tea_code
    , tea_name
    , state
    , tea_type_code
    , img_link
    , tenant_code
    , comment
    , extra_info
) VALUES (
    now()
    , now()
    , #{teaCode}
    , #{teaName}
    , #{state}
    , #{teaTypeCode}
    , #{imgLink}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE tea
SET
    gmt_modified = now()

        , tea_name = #{teaName}


        , state = #{state}


        , tea_type_code = #{teaTypeCode}


        , img_link = #{imgLink}


        , tenant_code = #{tenantCode}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND tea_code = #{teaCode}

mapper.delete:
DELETE FROM tea
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND tea_code = #{teaCode}
