mapper.select:
SELECT * FROM tea_unit
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND tea_code = #{teaCode}
    AND tea_unit_code = #{teaUnitCode}

SELECT * FROM tea_unit
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND tea_code = #{teaCode}

mapper.insert:
INSERT INTO tea_unit (
    gmt_created
    , gmt_modified
    , tenant_code
    , tea_code
    , tea_unit_code
    , tea_unit_name
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{teaCode}
    , #{teaUnitCode}
    , #{teaUnitName}
)

mapper.delete:
DELETE FROM tea_unit
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND tea_code = #{teaCode}
