mapper.select:
SELECT * FROM topping_base_rule
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND tea_code = #{teaCode}


        AND step_index = #{stepIndex}


        AND topping_code = #{toppingCode}

SELECT * FROM topping_base_rule
WHERE 1 = 1
AND tenant_code = #{tenantCode}

        AND tea_code = #{teaCode}

mapper.insert:
INSERT INTO topping_base_rule (
    gmt_created
    , gmt_modified
    , tenant_code
    , tea_code
    , step_index
    , topping_code
    , base_amount
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{teaCode}
    , #{stepIndex}
    , #{toppingCode}
    , #{baseAmount}
)

mapper.delete:
DELETE FROM topping_base_rule
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND tea_code = #{teaCode}
