mapper.select:
SELECT * FROM topping
WHERE 1 = 1
    and tenant_code = #{tenantCode}

        AND topping_code = #{toppingCode}

SELECT * FROM topping
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

SELECT COUNT(1) FROM topping
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND topping_type_code = #{toppingTypeCode}

SELECT * FROM topping
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND topping_name = #{toppingName}


        AND topping_code = #{toppingCode}

mapper.insert:
INSERT INTO topping (
    gmt_created
    , gmt_modified
    , topping_code
    , topping_name
    , topping_type_code
    , measure_unit
    , state
    , valid_period_hour
    , clean_period_hour
    , convert_coefficient
    , flow_speed
    , tenant_code
    , comment
    , extra_info
    , valid_period_warning_threshold_min
    , clean_period_warning_threshold_min
    , invalid_warning_threshold_g
    , over_amount
    , over_mode
    , under_amount
    , under_mode
) VALUES (
    now()
    , now()
    , #{toppingCode}
    , #{toppingName}
    , #{toppingTypeCode}
    , #{measureUnit}
    , #{state}
    , #{validPeriodHour}
    , #{cleanPeriodHour}
    , #{convertCoefficient}
    , #{flowSpeed}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
    , #{validPeriodWarningThresholdMin}
    , #{cleanPeriodWarningThresholdMin}
    , #{invalidWarningThresholdG}
    , #{overAmount}
    , #{overMode}
    , #{underAmount}
    , #{underMode}
)

mapper.update:
UPDATE topping
SET
    gmt_modified = now()

        , topping_name = #{toppingName}


        , topping_type_code = #{toppingTypeCode}


        , measure_unit = #{measureUnit}


        , state = #{state}


        , valid_period_hour = #{validPeriodHour}


        , clean_period_hour = #{cleanPeriodHour}


        , convert_coefficient = #{convertCoefficient}


        , flow_speed = #{flowSpeed}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}


        , valid_period_warning_threshold_min = #{validPeriodWarningThresholdMin}


        , clean_period_warning_threshold_min = #{cleanPeriodWarningThresholdMin}


        , invalid_warning_threshold_g = #{invalidWarningThresholdG}


        , over_mode = #{overMode}


        , over_amount = #{overAmount}


        , under_mode = #{underMode}


        , under_amount = #{underAmount}

WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_code = #{toppingCode}

mapper.delete:
DELETE FROM topping
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_code = #{toppingCode}
