mapper.select:
SELECT * FROM topping
WHERE 1 = 1
    and tenant_code = #{tenantCode}

        AND topping_name = #{toppingName}


        AND topping_code = #{toppingCode}

SELECT * FROM topping
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

SELECT * FROM topping
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND topping_name = #{toppingName}


        AND topping_code = #{toppingCode}

mapper.insert:
INSERT INTO `topping` (
    `gmt_created`
    , `gmt_modified`
    , `topping_code`
    , `topping_name`
    , `topping_type_code`
    , `measure_unit`
    , `state`
    , `valid_hour_period`
    , `clean_hour_period`
    , `convert_coefficient`
    , `flow_speed`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{toppingCode}
    , #{toppingName}
    , #{toppingTypeCode}
    , #{measureUnit}
    , #{state}
    , #{validHourPeriod}
    , #{cleanHourPeriod}
    , #{convertCoefficient}
    , #{flowSpeed}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE topping
SET
    gmt_modified = now()

        , topping_name = #{toppingName}


        , topping_type_code = #{toppingTypeCode}


        , measure_unit = #{measureUnit}


        , state = #{state}


        , valid_hour_period = #{validHourPeriod}


        , clean_hour_period = #{cleanHourPeriod}


        , convert_coefficient = #{convertCoefficient}


        , flow_speed = #{flowSpeed}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_code = #{toppingCode}

mapper.delete:
DELETE FROM topping
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_code = #{toppingCode}
