mapper.select:
SELECT * FROM menu_dispatch_history
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND init = #{init}
    AND file_name = #{fileName}

mapper.insert:
INSERT INTO menu_dispatch_history (
    gmt_created
    , gmt_modified
    , tenant_code
    , init
    , file_name
    , md5
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{init}
    , #{fileName}
    , #{md5}
)

mapper.delete:
DELETE FROM menu_dispatch_history
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND init = #{init}
    AND file_name = #{fileName}
