mapper.select:
SELECT * FROM menu_dispatch
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND menu_code = #{menuCode}


        AND shop_group_code = #{shopGroupCode}

SELECT * FROM menu_dispatch
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND menu_code = #{menuCode}

SELECT * FROM menu_dispatch
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND shop_group_code = #{shopGroupCode}

mapper.insert:
INSERT INTO menu_dispatch (
    gmt_created
    , gmt_modified
    , menu_code
    , shop_group_code
    , tenant_code
) VALUES (
    now()
    , now()
    , #{menuCode}
    , #{shopGroupCode}
    , #{tenantCode}
)

mapper.delete:
DELETE FROM menu_dispatch
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND menu_code = #{menuCode}
