mapper.select:
SELECT * FROM menu
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND menu_code = #{menuCode}

SELECT * FROM menu
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND menu_code IN

            #{menuCode}

SELECT * FROM menu
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND menu_code = #{menuCode}


        AND menu_name = #{menuName}

mapper.insert:
INSERT INTO menu (
    gmt_created
    , gmt_modified
    , menu_code
    , menu_name
    , valid_from
    , tenant_code
    , comment
    , extra_info
) VALUES (
    now()
    , now()
    , #{menuCode}
    , #{menuName}
    , #{validFrom}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE menu SET
    gmt_modified = now()

        , menu_name = #{menuName}


        , valid_from = #{validFrom}


        , tenant_code = #{tenantCode}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND menu_code = #{menuCode}

mapper.delete:
DELETE FROM menu
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND menu_code = #{menuCode}
