mapper.select:
SELECT * FROM menu_series_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND menu_code = #{menuCode}


        AND series_code = #{seriesCode}

SELECT * FROM menu_series_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND menu_code = #{menuCode}

SELECT COUNT(1) FROM menu_series_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND series_code = #{seriesCode}

mapper.insert:
INSERT INTO menu_series_rel (
    gmt_created
    , gmt_modified
    , menu_code
    , series_code
    , tenant_code
) VALUES (
    now()
    , now()
    , #{menuCode}
    , #{seriesCode}
    , #{tenantCode}
)

mapper.delete:
DELETE FROM menu_series_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND menu_code = #{menuCode}
