mapper.select:
SELECT * FROM series
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND series_code = #{seriesCode}

SELECT * FROM series
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

SELECT * FROM series
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND series_code = #{seriesCode}


        AND series_name = #{seriesName}

mapper.insert:
INSERT INTO series (
    gmt_created
    , gmt_modified
    , series_code
    , series_name
    , tenant_code
    , comment
    , extra_info
) VALUES (
    now()
    , now()
    , #{seriesCode}
    , #{seriesName}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE series
SET
    gmt_modified = now()

        , series_name = #{seriesName}


        , tenant_code = #{tenantCode}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND series_code = #{seriesCode}

mapper.delete:
DELETE FROM series
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND series_code = #{seriesCode}
