mapper.select:
SELECT * FROM clean_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND idempotent_mark = #{idempotentMark}

SELECT * FROM clean_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

SELECT * FROM clean_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND shop_group_code IN

            #{shopGroupCode}



        AND shop_code IN

            #{shopCode}

mapper.insert:
INSERT INTO clean_act_record (
    gmt_created
    , gmt_modified
    , tenant_code
    , extra_info
    , idempotent_mark
    , machine_code
    , shop_code
    , shop_group_code
    , clean_start_time
    , clean_end_time
    , topping_code
    , pipeline_num
    , clean_type
    , clean_rule_code
    , open_rule_code
    , close_rule_code
    , wash_sec
    , soak_min
    , flush_interval_min
    , flush_sec
    , clean_content
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
    , #{idempotentMark}
    , #{machineCode}
    , #{shopCode}
    , #{shopGroupCode}
    , #{cleanStartTime}
    , #{cleanEndTime}
    , #{toppingCode}
    , #{pipelineNum}
    , #{cleanType}
    , #{cleanRuleCode}
    , #{openRuleCode}
    , #{closeRuleCode}
    , #{washSec}
    , #{soakMin}
    , #{flushIntervalMin}
    , #{flushSec}
    , #{cleanContent}
)

mapper.delete:
DELETE FROM clean_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND idempotent_mark = #{idempotentMark}
