mapper.select:
SELECT * FROM drain_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND idempotent_mark = #{idempotentMark}

SELECT * FROM drain_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND shop_group_code IN

            #{shopGroupCode}



        AND shop_code IN

            #{shopCode}


ORDER BY gmt_modified DESC

mapper.insert:
INSERT INTO drain_act_record (
    gmt_created
    , gmt_modified
    , tenant_code
    , extra_info
    , idempotent_mark
    , machine_code
    , shop_code
    , shop_group_code
    , drain_start_time
    , drain_end_time
    , topping_code
    , pipeline_num
    , drain_type
    , drain_rule_code
    , flush_weight
    , flush_sec
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
    , #{idempotentMark}
    , #{machineCode}
    , #{shopCode}
    , #{shopGroupCode}
    , #{drainStartTime}
    , #{drainEndTime}
    , #{toppingCode}
    , #{pipelineNum}
    , #{drainType}
    , #{drainRuleCode}
    , #{flushWeight}
    , #{flushSec}
)

mapper.delete:
DELETE FROM drain_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND idempotent_mark = #{idempotentMark}
