mapper.select:
SELECT * FROM invalid_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND idempotent_mark = #{idempotentMark}

SELECT * FROM invalid_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND shop_group_code IN

            #{shopGroupCode}



        AND shop_code IN

            #{shopCode}


ORDER BY gmt_modified DESC

mapper.insert:
INSERT INTO invalid_act_record (
    gmt_created
    , gmt_modified
    , machine_code
    , shop_code
    , shop_group_code
    , invalid_time
    , topping_code
    , pipeline_num
    , invalid_amount
    , tenant_code
    , idempotent_mark
    , extra_info
) VALUES (
    now()
    , now()
    , #{machineCode}
    , #{shopCode}
    , #{shopGroupCode}
    , #{invalidTime}
    , #{toppingCode}
    , #{pipelineNum}
    , #{invalidAmount}
    , #{tenantCode}
    , #{idempotentMark}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.delete:
DELETE FROM invalid_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND idempotent_mark = #{idempotentMark}
