mapper.select:
SELECT * FROM order_specitem_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND shop_group_code = #{shopGroupCode}
    AND idempotent_mark = #{idempotentMark}

mapper.insert:
INSERT INTO order_specitem_act_record (
    gmt_created
    , gmt_modified
    , tenant_code
    , shop_group_code
    , idempotent_mark
    , spec_code
    , spec_item_code
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{shopGroupCode}
    , #{idempotentMark}
    , #{specCode}
    , #{specItemCode}
)

mapper.delete:
DELETE FROM order_specitem_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND shop_group_code = #{shopGroupCode}
    AND idempotent_mark = #{idempotentMark}
