mapper.select:
SELECT * FROM order_topping_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND idempotent_mark = #{idempotentMark}

mapper.insert:
INSERT INTO order_topping_act_record (
    gmt_created
    , gmt_modified
    , tenant_code
    , idempotent_mark
    , step_index
    , topping_code
    , actual_amount
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{idempotentMark}
    , #{stepIndex}
    , #{toppingCode}
    , #{actualAmount}
)

mapper.delete:
DELETE FROM order_topping_act_record
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND idempotent_mark = #{idempotentMark}
