mapper.select:
SELECT * FROM order_amt_report
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND order_created_day = #{orderCreatedDay}

SELECT * FROM order_amt_report
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND order_created_day IN

            #{orderCreatedDay}

mapper.insert:
INSERT INTO order_amt_report (
    gmt_created
    , gmt_modified
    , tenant_code
    , order_created_day
    , amount
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{orderCreatedDay}
    , #{amount}
)

mapper.delete:
DELETE FROM order_amt_report
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND order_created_day = #{orderCreatedDay}
