mapper.select:
SELECT * FROM order_topping_report_by_shop
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND order_created_day = #{orderCreatedDay}

SELECT * FROM order_topping_report_by_shop
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND order_created_day IN

            #{orderCreatedDay}

mapper.insert:
INSERT INTO order_topping_report_by_shop (
    gmt_created
    , gmt_modified
    , tenant_code
    , order_created_day
    , shop_group_code
    , shop_code
    , topping_code
    , amount
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{orderCreatedDay}
    , #{shopGroupCode}
    , #{shopCode}
    , #{toppingCode}
    , #{amount}
)

mapper.delete:
DELETE FROM order_topping_report_by_shop
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND order_created_day = #{orderCreatedDay}
