mapper.select:
SELECT * FROM clean_act_record
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and machine_code = #{machineCode}
    and shop_code = #{shopCode}
    and clean_start_time = #{cleanStartTime}
    and topping_code = #{toppingCode}
    and pipeline_num = #{pipelineNum}

mapper.insert:
INSERT INTO `clean_act_record` (
    `gmt_created`
    , `gmt_modified`
    , `machine_code`
    , `shop_code`
    , `clean_start_time`
    , `clean_end_time`
    , `clean_type`
    , `clean_content`
    , `topping_code`
    , `pipeline_num`
    , `tenant_code`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{machineCode}
    , #{shopCode}
    , #{cleanStartTime}
    , #{cleanEndTime}
    , #{cleanType}
    , #{cleanContent}
    , #{toppingCode}
    , #{pipelineNum}
    , #{tenantCode}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.delete:
DELETE FROM clean_act_record
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and machine_code = #{machineCode}
    and shop_code = #{shopCode}
    and clean_start_time = #{cleanStartTime}
    and topping_code = #{toppingCode}
    and pipeline_num = #{pipelineNum}
