mapper.select:
SELECT * FROM clean_rule_step
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND clean_rule_code = #{cleanRuleCode}
    AND step_index = #{stepIndex}

SELECT * FROM clean_rule_step
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND clean_rule_code = #{cleanRuleCode}

mapper.insert:
INSERT INTO clean_rule_step (
    gmt_created
    , gmt_modified
    , clean_rule_code
    , step_index
    , clean_content
    , wash_sec
    , soak_min
    , flush_interval_min
    , flush_sec
    , remind_title
    , remind_content
    , need_confirm
    , clean_agent_type
    , tenant_code
) VALUES (
    now()
    , now()
    , #{cleanRuleCode}
    , #{stepIndex}
    , #{cleanContent}
    , #{washSec}
    , #{soakMin}
    , #{flushIntervalMin}
    , #{flushSec}
    , #{remindTitle}
    , #{remindContent}
    , #{needConfirm}
    , #{cleanAgentType}
    , #{tenantCode}
)

mapper.update:
UPDATE clean_rule_step
SET
    gmt_modified = now()

        , clean_content = #{cleanContent}


        , wash_sec = #{washSec}


        , soak_min = #{soakMin}


        , flush_interval_min = #{flushIntervalMin}


        , flush_sec = #{flushSec}


        , remind_title = #{remindTitle}


        , remind_content = #{remindContent}


        , clean_agent_type= #{cleanAgentType}

WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND clean_rule_code = #{cleanRuleCode}
    AND step_index = #{stepIndex}

mapper.delete:
DELETE FROM clean_rule_step
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND clean_rule_code = #{cleanRuleCode}
