mapper.select:
SELECT * FROM drain_rule_dispatch
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND drain_rule_code = #{drainRuleCode}


        AND shop_group_code = #{shopGroupCode}

SELECT * FROM drain_rule_dispatch
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND drain_rule_code = #{drainRuleCode}

mapper.insert:
INSERT INTO drain_rule_dispatch (
    gmt_created
    , gmt_modified
    , drain_rule_code
    , shop_group_code
    , tenant_code
) VALUES (
    now()
    , now()
    , #{drainRuleCode}
    , #{shopGroupCode}
    , #{tenantCode}
)

mapper.delete:
DELETE FROM drain_rule_dispatch
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND drain_rule_code = #{drainRuleCode}
