mapper.select:
SELECT * FROM open_rule_dispatch
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND open_rule_code = #{openRuleCode}


        AND shop_group_code = #{shopGroupCode}

SELECT * FROM open_rule_dispatch
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND open_rule_code = #{openRuleCode}

mapper.insert:
INSERT INTO open_rule_dispatch (
    gmt_created
    , gmt_modified
    , open_rule_code
    , shop_group_code
    , tenant_code
) VALUES (
    now()
    , now()
    , #{openRuleCode}
    , #{shopGroupCode}
    , #{tenantCode}
)

mapper.delete:
DELETE FROM open_rule_dispatch
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND open_rule_code = #{openRuleCode}
