mapper.select:
SELECT * FROM clean_rule_except
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND clean_rule_code = #{cleanRuleCode}

mapper.insert:
INSERT INTO clean_rule_except (
    gmt_created
    , gmt_modified
    , clean_rule_code
    , except_topping_code
    , tenant_code
) VALUES (
    now()
    , now()
    , #{cleanRuleCode}
    , #{exceptToppingCode}
    , #{tenantCode}
)

mapper.delete:
DELETE FROM clean_rule_except
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND clean_rule_code = #{cleanRuleCode}
