mapper.select:
SELECT * FROM clean_except_rule
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND clean_rule_code = #{cleanRuleCode}
    AND except_topping_code = #{exceptToppingCode}

SELECT * FROM clean_except_rule
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND clean_rule_code = #{cleanRuleCode}

mapper.insert:
INSERT INTO clean_except_rule (
    gmt_created
    , gmt_modified
    , clean_rule_code
    , except_topping_code
    , tenant_code
) VALUES (
    now()
    , now()
    , #{cleanRuleCode}
    , #{exceptToppingCode}
    , #{tenantCode}
)

mapper.delete:
DELETE FROM clean_except_rule
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND clean_rule_code = #{cleanRuleCode}
