mapper.select:
SELECT * FROM open_rule_topping
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND open_rule_code = #{openRuleCode}

mapper.insert:
INSERT INTO open_rule_topping (
    gmt_created
    , gmt_modified
    , open_rule_code
    , topping_code
    , flush_time
    , flush_weight
    , tenant_code
) VALUES (
    now()
    , now()
    , #{openRuleCode}
    , #{toppingCode}
    , #{flushTime}
    , #{flushWeight}
    , #{tenantCode}
)

mapper.delete:
DELETE FROM open_rule_topping
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND open_rule_code = #{openRuleCode}
