mapper.select:
SELECT * FROM warning_rule
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and warning_rule_code = #{warningRuleCode}

mapper.insert:
INSERT INTO `warning_rule` (
    `gmt_created`
    , `gmt_modified`
    , `warning_rule_code`
    , `warning_rule_name`
    , `warning_type`
    , `warning_content`
    , `threshold_type`
    , `threshold`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{warningRuleCode}
    , #{warningRuleName}
    , #{warningType}
    , #{warningContent}
    , #{thresholdType}
    , #{threshold}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE warning_rule
SET
    gmt_modified = now()

        , warning_rule_name = #{warningRuleName}


        , warning_type = #{warningType}


        , warning_content = #{warningContent}


        , threshold_type = #{thresholdType}


        , threshold = #{threshold}


        , tenant_code = #{tenantCode}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and warning_rule_code = #{warningRuleCode}

mapper.delete:
DELETE FROM warning_rule
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and warning_rule_code = #{warningRuleCode}
