mapper.select:
SELECT * FROM series_tea_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND series_code = #{seriesCode}


        AND tea_code = #{teaCode}

SELECT * FROM series_tea_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND series_code = #{seriesCode}

mapper.insert:
INSERT INTO series_tea_rel (
    gmt_created
    , gmt_modified
    , series_code
    , tea_code
    , tenant_code
) VALUES (
    now()
    , now()
    , #{seriesCode}
    , #{teaCode}
    , #{tenantCode}
)

mapper.delete:
DELETE FROM series_tea_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND series_code = #{seriesCode}
