mapper.select:
SELECT * FROM shop
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND shop_code = #{shopCode}

SELECT * FROM shop
WHERE 1 = 1
AND tenant_code = #{tenantCode}

    AND shop_group_code IN

        #{shopGroupCode}

SELECT * FROM shop
WHERE 1 = 1

        AND shop_name = #{shopName}


        AND tenant_code = #{tenantCode}


        AND shop_group_code IN

            #{shopGroupCode}

mapper.insert:
INSERT INTO shop (
    gmt_created
    , gmt_modified
    , shop_code
    , shop_name
    , shop_type
    , shop_group_code
    , tenant_code
    , comment
    , extra_info
) VALUES (
    now()
    , now()
    , #{shopCode}
    , #{shopName}
    , #{shopType}
    , #{shopGroupCode}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE shop
SET
    gmt_modified = now()

        , shop_name = #{shopName}


        , shop_type = #{shopType}


        , shop_group_code = #{shopGroupCode}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and shop_code = #{shopCode}

mapper.delete:
DELETE FROM shop
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and shop_code = #{shopCode}

mapper.select:
SELECT COUNT(1) FROM shop
WHERE 1 = 1
AND tenant_code = #{tenantCode}

    AND shop_group_code = #{shopGroupCode}
