mapper.select:
SELECT * FROM spec_item
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND spec_item_code = #{specItemCode}


        AND spec_item_name = #{specItemName}

SELECT * FROM spec_item
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND spec_code = #{specCode}

mapper.insert:
INSERT INTO spec_item (
    gmt_created
    , gmt_modified
    , spec_code
    , spec_item_code
    , spec_item_name
    , outer_spec_item_code
    , tenant_code
) VALUES (
    now()
    , now()
    , #{specCode}
    , #{specItemCode}
    , #{specItemName}
    , #{outerSpecItemCode}
    , #{tenantCode}
)

mapper.update:
UPDATE spec_item
SET
    gmt_modified = now()

    , spec_code = #{specCode}


    , spec_item_name = #{specItemName}


    , outer_spec_item_code = #{outerSpecItemCode}

WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND spec_item_code = #{specItemCode}

mapper.delete:
DELETE FROM spec_item
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND spec_code = #{specCode}


        AND spec_item_code = #{specItemCode}
