mapper.select:
SELECT * FROM spec
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND spec_code = #{specCode}


        AND spec_name = #{specName}

SELECT * FROM spec
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

SELECT * FROM spec
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND spec_code = #{specCode}


        AND spec_name = #{specName}

mapper.insert:
INSERT INTO spec (
    gmt_created
    , gmt_modified
    , spec_code
    , spec_name
    , state
    , tenant_code
    , comment
    , extra_info
) VALUES (
    now()
    , now()
    , #{specCode}
    , #{specName}
    , #{state}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE spec
SET
    gmt_modified = now()

        , spec_name = #{specName}


        , state = #{state}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND spec_code = #{specCode}

mapper.delete:
DELETE FROM spec
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND spec_code = #{specCode}
