mapper.select:
SELECT * FROM spec_sub
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND spec_sub_code = #{specSubCode}


        AND spec_sub_name = #{specSubName}

SELECT * FROM spec_sub
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND spec_code = #{specCode}

mapper.insert:
INSERT INTO spec_sub (
    gmt_created
    , gmt_modified
    , spec_code
    , spec_sub_code
    , spec_sub_name
    , outer_spec_sub_code
    , tenant_code
) VALUES (
    now()
    , now()
    , #{specCode}
    , #{specSubCode}
    , #{specSubName}
    , #{outerSpecSubCode}
    , #{tenantCode}
)

mapper.update:
UPDATE spec_sub
SET
    gmt_modified = now()

    , spec_code = #{specCode}


    , spec_sub_name = #{specSubName}


    , outer_spec_sub_code = #{outerSpecSubCode}

WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND spec_sub_code = #{specSubCode}

mapper.delete:
DELETE FROM spec_sub
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND spec_code = #{specCode}


        AND spec_sub_code = #{specSubCode}
