mapper.select:
SELECT * FROM tea
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and tea_code = #{teaCode}

mapper.insert:
INSERT INTO `tea` (
    `gmt_created`
    , `gmt_modified`
    , `tea_code`
    , `tea_name`
    , `outer_tea_code`
    , `state`
    , `tea_type_code`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{teaCode}
    , #{teaName}
    , #{outerTeaCode}
    , #{state}
    , #{teaTypeCode}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE tea
SET
    gmt_modified = now()

        , teaName = #{teaName}


        , outer_tea_code = #{outerTeaCode}


        , state = #{state}


        , tea_type_code = #{teaTypeCode}


        , tenant_code = #{tenantCode}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and tea_code = #{teaCode}

mapper.delete:
DELETE FROM tea
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and tea_code = #{teaCode}
