mapper.select:
SELECT * FROM tea_topping_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND tea_code = #{teaCode}
    AND topping_code = #{toppingCode}

SELECT * FROM tea_topping_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND tea_code = #{teaCode}

mapper.insert:
INSERT INTO tea_topping_rel (
    gmt_created
    , gmt_modified
    , tea_code
    , topping_code
    , amount
    , tenant_code
) VALUES (
    now()
    , now()
    , #{teaCode}
    , #{toppingCode}
    , #{amount}
    , #{tenantCode}
)

mapper.update:
UPDATE tea_topping_rel
SET
    gmt_modified = now()

        , amount = #{amount}

WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND tea_code = #{teaCode}
    AND topping_code = #{toppingCode}

mapper.delete:
DELETE FROM tea_topping_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND tea_code = #{teaCode}


        AND topping_code = #{toppingCode}
