mapper.select:
SELECT * FROM tea_unit
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND tea_code = #{teaCode}


        AND tea_unit_code = #{teaUnitCode}


        AND tea_unit_name = #{teaUnitName}

SELECT * FROM tea_unit
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND tea_code = #{teaCode}

mapper.insert:
INSERT INTO tea_unit (
    gmt_created
    , gmt_modified
    , tea_unit_code
    , tea_unit_name
    , tea_code
    , spec_item_code
    , tenant_code
) VALUES (
    now()
    , now()
    , #{teaUnitCode}
    , #{teaUnitName}
    , #{teaCode}
    , #{specItemCode}
    , #{tenantCode}
)

mapper.update:
UPDATE tea_unit
SET
    gmt_modified = now()

        , tea_unit_code = #{teaUnitCode}


        , tea_unit_name = #{teaUnitName}


        , tea_code = #{teaCode}


        , spec_item_code = #{specItemCode}

WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND tea_code = #{teaCode}
    AND tea_unit_code = #{teaUnitCode}

mapper.delete:
DELETE FROM tea_unit
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND tea_code = #{teaCode}
