mapper.select:
SELECT * FROM role
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND role_code = #{roleCode}


        AND role_name = #{roleName}

SELECT *
FROM role
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

        AND role_name = #{roleName}

mapper.insert:
INSERT INTO role (
    gmt_created
    , gmt_modified
    , role_code
    , role_name
    , sys_reserved
    , tenant_code
    , comment
    , extra_info
) VALUES (
    now()
    , now()
    , #{roleCode}
    , #{roleName}
    , #{sysReserved}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE role
SET
    gmt_modified = now()

        , role_name = #{roleName}


        , sys_reserved = #{sysReserved}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and role_code = #{roleCode}

mapper.delete:
DELETE FROM role
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND role_code = #{roleCode}
