mapper.select:
SELECT * FROM tenant
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

SELECT *
FROM tenant
WHERE 1 = 1

    AND tenant_name = #{tenantName}


    AND contact_person = #{contactPerson}

mapper.insert:
INSERT INTO `tenant` (
    `gmt_created`
    , `gmt_modified`
    , `tenant_code`
    , `tenant_name`
    , `contact_person`
    , `contact_phone`
    , `img_link`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{tenantCode}
    , #{tenantName}
    , #{contactPerson}
    , #{contactPhone}
    , #{imgLink}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE tenant SET
    gmt_modified = now()

        , tenant_name = #{tenantName}


        , contact_person = #{contactPerson}


        , contact_phone = #{contactPhone}


        , img_link = #{imgLink}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    AND tenant_code = #{tenantCode};
