mapper.select:
SELECT * FROM admin_role
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and role_code = #{roleCode}

mapper.insert:
INSERT INTO `admin_role` (
    `gmt_created`
    , `gmt_modified`
    , `role_code`
    , `role_name`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{roleCode}
    , #{roleName}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE admin_role
SET
    gmt_modified = now()

        , role_name = #{roleName}


        , comment = #{comment}


        , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}

WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and role_code = #{roleCode}

mapper.delete:
DELETE FROM admin_role
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and role_code = #{roleCode}
